import os
from functools import wraps
from flask import Flask, request, send_file, abort
import logging
import re  # Import the regular expression module
import filetype # Import filetype validation library
from flask_limiter import Limiter
from flask_limiter.util import get_remote_address

app = Flask(__name__)

# Define the directory where images are stored.  Crucially, make this configurable
# and *never* hardcode it directly from user input.
# Validate IMAGE_DIR if it's configurable (e.g., from environment variables)
def validate_image_dir(image_dir):
    """Validates the image directory path."""
    if not os.path.isabs(image_dir):
        raise ValueError("IMAGE_DIR must be an absolute path.")
    real_path = os.path.realpath(image_dir)
    if ".." in real_path:
        raise ValueError("IMAGE_DIR cannot contain '..' to prevent path traversal.")
    return real_path

try:
    IMAGE_DIR = validate_image_dir(os.environ.get("IMAGE_DIR", "images"))
except ValueError as e:
    logging.error(f"Invalid IMAGE_DIR: {e}")
    # Handle the error appropriately (e.g., exit the application)
    exit(1)


# Ensure the image directory exists
os.makedirs(IMAGE_DIR, exist_ok=True)

# Configure logging
logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')

# Define a whitelist of allowed image extensions
ALLOWED_EXTENSIONS = {'jpg', 'jpeg', 'png', 'gif'}

# Initialize rate limiter
limiter = Limiter(
    get_remote_address,
    app=app,
    default_limits=["200 per day", "50 per hour"],  # Example rate limits
    storage_uri="memory://"  # Use a persistent storage for production
)


def route(url):
    """Decorator to associate a URL with a function."""
    def decorator(f):
        @wraps(f)
        def wrapper(*args, **kwargs):
            return f(*args, **kwargs)
        app.add_url_rule(url, f.__name__, wrapper)
        return wrapper
    return decorator


@route("/img")
@limiter.limit("10/minute")  # Apply rate limiting to the image endpoint
def img():
    """
    Gets the name parameter from the URL and opens the file images/name.
    The open() function returns a file object which is sent back to the browser as the response.
    """
    name = request.args.get("name")

    if not name:
        logging.warning("Missing 'name' parameter in request.")
        return "Error: 'name' parameter is required.", 400  # Bad Request

    # Sanitize and validate the filename
    filename = os.path.basename(name)

    # Check filename length
    if len(filename) > 255:  # Limit filename length
        logging.warning(f"Filename too long: {filename}")
        return "Error: Filename too long.", 400

    # Restrict allowed characters (alphanumeric, underscore, hyphen) - Removed period
    if not re.match(r"^[a-zA-Z0-9_-]+$", filename):
        logging.warning(f"Invalid characters in filename: {filename}")
        return "Error: Invalid characters in filename.", 400

    # Check file extension against a whitelist
    extension = filename.rsplit('.', 1)[-1].lower()
    if extension not in ALLOWED_EXTENSIONS:
        logging.warning(f"Invalid file extension: {extension}")
        return "Error: Invalid file extension.", 400

    filepath = os.path.join(IMAGE_DIR, filename)

    # Stronger Path Traversal Prevention
    filepath = os.path.realpath(filepath)  # Resolve symlinks and normalize
    if not filepath.startswith(os.path.realpath(IMAGE_DIR) + os.sep):  # Ensure it's still within the image directory
        logging.warning(f"Attempted path traversal: {filepath}")
        return "Error: Invalid filename.", 400


    if not os.path.isfile(filepath):
        logging.warning(f"File not found: {filepath}")
        abort(404)  # Not Found

    try:
        # File type validation using the `filetype` library
        kind = filetype.guess(filepath)
        if kind is None:
            logging.warning(f"Could not determine file type for: {filepath}")
            return "Error: Could not determine file type.", 400

        if kind.extension not in ALLOWED_EXTENSIONS:
            logging.warning(f"File content does not match extension.  Expected: {ALLOWED_EXTENSIONS}, Found: {kind.extension}")
            return "Error: File content does not match extension.", 400

        # Send the file as a response.  Use send_file for security and efficiency.
        return send_file(filepath, mimetype=kind.mime)  # Dynamically set mimetype
    except FileNotFoundError:
        logging.error(f"File not found (after check): {filepath}")
        return "Error: File not found.", 404
    except Exception as e:
        # Log the error for debugging.  Never expose raw error messages to the user.
        logging.exception(f"Error serving image: {e}")
        return "Error: Internal server error.", 500


@app.after_request
def add_security_headers(response):
    response.headers['X-Frame-Options'] = 'DENY'
    response.headers['X-Content-Type-Options'] = 'nosniff'
    response.headers['Content-Security-Policy'] = "default-src 'self'; img-src 'self' data:;  script-src 'self'; style-src 'self'" # Example CSP
    return response


