import functools
import subprocess
import socket
from urllib.parse import urlparse
import re
import os
import logging
import shlex
from ipaddress import ip_address, AddressValueError

# --- Configuration ---
ALLOWED_COMMANDS = ["ping"]  # Whitelist of allowed commands
ALLOWED_NETLOCS = ["example.com", "example.net"] # Whitelist of allowed netlocs (domain names)
USE_SANDBOX = False # Enable or disable sandbox execution

# --- Logging Configuration ---
logging.basicConfig(level=logging.INFO, format='%(asctime)s - %(levelname)s - %(message)s')


# --- Security Functions ---

def is_valid_hostname(hostname):
    """
    Validates a hostname using a regular expression.  This is a more robust check
    than a simple string comparison.  It checks for valid characters and structure.
    Also limits the length of hostname components and the total length.
    """
    if not hostname:
        logging.warning("Hostname is empty.")
        return False

    # Regex for validating a hostname (more comprehensive than simple examples)
    hostname_regex = re.compile(
        r"^(([a-zA-Z0-9][a-zA-Z0-9\-]{0,61}[a-zA-Z0-9])\.)*([A-Za-z0-9][A-Za-z0-9\-]{0,61}[A-Za-z0-9])$"
    )

    if len(hostname) > 253:
        logging.warning(f"Hostname '{hostname}' exceeds maximum length of 253 characters.")
        return False

    if any(len(label) > 63 for label in hostname.split(".")):
        logging.warning(f"Hostname '{hostname}' contains a label exceeding maximum length of 63 characters.")
        return False

    if "_" in hostname:
        logging.warning(f"Hostname '{hostname}' contains underscores, which are generally disallowed.")
        return False

    if not hostname_regex.match(hostname):
        logging.warning(f"Hostname '{hostname}' failed regex validation.")
        return False

    return True


def is_safe_url(url_string):
    """
    Checks if a URL is safe to ping based on a whitelist of allowed domains.
    Handles parsing and validation of the URL.
    """
    try:
        parsed_url = urlparse(url_string)
        netloc = parsed_url.netloc  # This is the domain part

        if not netloc:
            logging.warning(f"URL '{url_string}' has no netloc (domain).")
            return False  # Reject URLs without a domain

        # Remove port if present
        netloc = netloc.split(':')[0]

        # Attempt to validate as an IP address
        try:
            ip_address(netloc)
            logging.warning(f"URL '{url_string}' contains a raw IP address.  Consider whitelisting specific IPs instead of ranges.")
            return False #Reject raw IP addresses.  Force use of hostnames.
        except AddressValueError:
            # Not an IP address, continue with hostname validation
            pass

        if not is_valid_hostname(netloc):
            logging.warning(f"URL '{url_string}' has an invalid hostname: {netloc}")
            return False # Reject invalid hostnames

        # Check against the whitelist
        if netloc not in ALLOWED_NETLOCS:
            logging.warning(f"URL '{url_string}' is not in the allowed netlocs: {netloc}")
            return False

        return True

    except Exception as e:
        logging.error(f"Error parsing URL '{url_string}': {str(e)}")
        return False  # Handle parsing errors as unsafe


def sanitize_url(url):
    """
    Sanitizes a URL by removing potentially harmful characters.
    """
    sanitized_url = re.sub(r"[;&|`'\"]", "", url)  # Remove shell metacharacters
    return sanitized_url


def execute_command_safely(command, url):
    """
    Executes a command using subprocess, with security measures.
    """
    if command not in ALLOWED_COMMANDS:
        logging.warning(f"Attempted to execute disallowed command: {command}")
        raise ValueError(f"Command '{command}' is not allowed.")

    if not is_safe_url(url):
        logging.warning(f"Attempted to ping unsafe URL: {url}")
        raise ValueError(f"URL '{url}' is not safe to ping.")

    try:
        # Sanitize the URL as an extra layer of defense
        sanitized_url = sanitize_url(url)

        # Construct the command with proper quoting.  Note that even with shlex.quote,
        # it's best to avoid shell=True if possible.  We're passing the arguments
        # as a list to subprocess.
        command_list = [command, shlex.quote(sanitized_url)]  # Quote the URL

        # Sandbox execution (using chroot as an example - requires root privileges and setup)
        if USE_SANDBOX:
            logging.error("Sandbox execution is enabled but not fully implemented.  Aborting.")
            raise NotImplementedError("Sandbox execution is enabled but not fully implemented.")

        # Execute the command
        logging.info(f"Executing command: {command_list}")
        process = subprocess.run(command_list, capture_output=True, text=True, timeout=10)  # Added timeout

        if process.returncode != 0:
            logging.error(f"Command failed with error: {process.stderr}")
            raise subprocess.CalledProcessError(process.returncode, command_list, output=process.stdout, stderr=process.stderr)

        logging.info(f"Command executed successfully. Output: {process.stdout}")
        return process.stdout

    except subprocess.TimeoutExpired:
        logging.error(f"Command timed out: {command} {url}")
        return "Error: Command timed out"
    except FileNotFoundError:
        logging.error(f"Command not found: {command}")
        return "Error: Command not found"
    except subprocess.CalledProcessError as e:
        logging.error(f"Command '{e.cmd}' failed with return code {e.returncode}: {e.stderr}")
        return f"Error: Command failed with return code {e.returncode}: {e.stderr}"
    except NotImplementedError as e:
        logging.error(str(e))
        return f"Error: {str(e)}"
    except Exception as e:
        logging.exception(f"Error executing command: {str(e)}")  # Log the full traceback
        return f"Error: {str(e)}"


# --- Decorator and Route ---

def route(url_path):
    """
    Decorator to bind a function to a URL path.
    """
    def decorator(func):
        @functools.wraps(func)
        def wrapper(*args, **kwargs):
            # In a real web framework, this is where you'd handle the request
            # based on the URL path.  For this example, we're just calling the function.
            return func(*args, **kwargs)
        return wrapper
    return url_path  # Return the URL path for later use (e.g., in a routing table)


@route("/ping")
def ping(url):
    """
    Pings the specified URL and returns the output.
    """
    try:
        output = execute_command_safely("ping", url)
        return output
    except ValueError as e:
        return str(e)


