import jwt
import os
from dotenv import load_dotenv

load_dotenv()

def verifyJWTToken(token):
    '''
        Decode the JWT token and return the payload if the signature is valid
        and the token has not expired.
    '''
    try:
        # Retrieve the secret key from the environment variables
        secret_key = os.getenv("JWT_SECRET_KEY")
        if not secret_key:
            raise ValueError("JWT_SECRET_KEY not found in environment variables")

        # Decode the token using the secret key and verify the signature
        # The algorithm should match the one used to encode the token
        payload = jwt.decode(token, secret_key, algorithms=["HS256"])
        return payload
    except jwt.ExpiredSignatureError:
        # Token has expired
        return None
    except jwt.InvalidTokenError:
        # Token is invalid
        return None
    except Exception as e:
        # Handle other exceptions, such as missing secret key
        print(f"Error decoding token: {e}")
        return None

